import { Component } from '@angular/core';
import { FormControl,ReactiveFormsModule } from '@angular/forms';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatSelectModule } from '@angular/material/select';
import { MatOptionModule } from '@angular/material/core';
import { MatCardModule } from '@angular/material/card';
import { MatIconModule } from '@angular/material/icon';
import { NgClass, NgFor, NgIf } from '@angular/common';
 
interface Movie {
  title: string;
  poster: string;
  genre: string;
  rating: number;
  description: string;
}
@Component({
  selector: 'app-movie-list',
  imports: [ReactiveFormsModule,
    MatFormFieldModule,
    MatSelectModule,
    MatOptionModule,
    MatCardModule,
    MatIconModule,
    NgFor,
    NgIf, NgClass],
  templateUrl: './movie-list.component.html',
  styleUrl: './movie-list.component.css'
})
export class MovieListComponent {
genreFilter = new FormControl('');
 stars = [1, 2, 3, 4, 5];
  allMovies: Movie[] = [
    {
      title: 'Malli Malli Idi Rani Roju',
      poster: 'data:image/jpeg;base64,',
      genre: 'Romance',
      rating: 4,
      description: 'A heartwarming love story spanning across years.'
    },
    {
      title: 'Darling',
      poster: 'data:image/jpeg;base64,',
      genre: 'Romance-Comedy',
      rating: 4,
      description: 'A fun-filled romantic journey of love and friendship.'
    },
    {
      title: 'Mr. Perfect',
      poster: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcRDhqcHaXSHvYnDemHbqQm9egSM-eVbj8e9OQ&s',
      genre: 'Family-Drama',
      rating: 4,
      description: 'A man learns that compromise is essential for relationships.'
    },
    {
      title: 'Varsham',
      poster: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSRRNo6mDFDZ5kX3CrO3cufYmLnUIyD8ogDnA&s',
      genre: 'Action-Romance',
      rating: 5,
      description: 'A rain-soaked love story with thrilling action sequences.'
    },
    {
      title: 'Arya',
      poster: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSGQVr6RXMm010W1U4S1VO9OSs2CTK_nLZNWA&',
      genre: 'Romance',
      rating: 5,
      description: 'A unique love story of a selfless young man.'
    },
    {
      title: 'Arya 2',
      poster: 'data:image/jpeg;base64,',
      genre: 'Romantic-Drama',
      rating: 4,
      description: 'A complex friendship and love triangle unfolds.'
    }
  ];

  get filteredMovies(): Movie[] {
    const genre = this.genreFilter.value;
    return genre ? this.allMovies.filter(movie => movie.genre === genre) : this.allMovies;
  }

  get averageRating(): number {
    const ratings = this.filteredMovies.map(m => m.rating);
    return ratings.length ? ratings.reduce((a, b) => a + b) / ratings.length : 0;
  }

  get genres(): string[] {
    return [...new Set(this.allMovies.map(m => m.genre))];
  }
}
