import { Component } from '@angular/core';
import { FormsModule, ReactiveFormsModule,FormControl } from '@angular/forms';
import { CommonModule } from '@angular/common';
import { MatToolbarModule } from '@angular/material/toolbar';
import { MatFormFieldModule } from '@angular/material/form-field';
import { MatInputModule } from '@angular/material/input';
import { MatSelectModule } from '@angular/material/select';
import { MatCardModule } from '@angular/material/card';
import { MatButtonModule } from '@angular/material/button';
import { MatIconModule } from '@angular/material/icon';

@Component({
  selector: 'app-song-list',
  imports: [
  CommonModule,
    FormsModule,
    ReactiveFormsModule,
    MatToolbarModule,
    MatFormFieldModule,
    MatInputModule,
    MatSelectModule,
    MatCardModule,
    MatButtonModule,
    MatIconModule
  ],
  templateUrl: './song-list.component.html',
  styleUrl: './song-list.component.css'
})
export class SongListComponent {
songs = [
    { 
      name: 'Po Pove Yekantham', 
      artist: 'Dhanunjay', album: 'Raghuvaran B.Tech', durationSec: 221, rating: 4.7,
      imageUrl: 'data:image/jpeg;base64,',
      audioUrl: 'https://pendujatt.com.se/song/po-pove-yekantham-anirudh-ravichander-mp3-song'
    },
    { 
      name: 'Rendu Kallu',     
      artist: 'Armaan Malik', album: 'Mahanubhavudu', durationSec: 211, rating: 4.4,
      imageUrl: 'data:image/jpeg;base64,',
      audioUrl: 'https://raagjatt.com.se/telugu-song/rendu-kallu-armaan-malik.html'
    },
    { 
      name: 'Chandamama Navve',         
      artist: 'Haricharan', album: 'Businessman', durationSec: 179, rating: 4.6,
      imageUrl: 'data:image/jpeg;base64,',
      audioUrl: 'https://masstelugu.com/2943/chandamama-navve-mp3-song?ref=redirect'
    }
   
  ];
 selectedArtist: string = '';
  selectedAlbum: string = '';

  // Unique artists & albums
  get uniqueArtists() {
    return [...new Set(this.songs.map(s => s.artist))];
  }

  get uniqueAlbums() {
    return [...new Set(this.songs.map(s => s.album))];
  }

  // Filtered songs
  get filteredSongs() {
    return this.songs.filter(song =>
      (this.selectedArtist ? song.artist === this.selectedArtist : true) &&
      (this.selectedAlbum ? song.album === this.selectedAlbum : true)
    );
  }

  // Helpers
  round(r: number) {
    return Math.round(r);
  }

  formatDuration(seconds: number) {
    const m = Math.floor(seconds / 60);
    const s = seconds % 60;
    return `${m}:${s.toString().padStart(2, '0')}`;
  }

  isTopRated(rating: number) {
    return rating >= 4.5;
  }

  // Audio player
  currentSong: string | null = null;
  audio: HTMLAudioElement | null = null;

  playSong(song: any) {
    this.stopSong(); // stop previous if playing
    if (typeof window !== 'undefined') {
      this.audio = new window.Audio(song.audioUrl);
      this.audio.play();
      this.currentSong = song.name;
    }
  }

  stopSong() {
    if (this.audio) {
      this.audio.pause();
      this.audio.currentTime = 0;
      this.audio = null;
    }
    this.currentSong = null;
  }
}
